import pandas as pd
import json
import csv
import re
import logging
from typing import List, Dict, Any, Tuple
from pathlib import Path
import streamlit as st

logger = logging.getLogger(__name__)

def process_medical_documents(uploaded_file) -> List[Dict[str, Any]]:
    """
    Process uploaded medical documents and extract passages
    Returns list of document passages with metadata
    """
    passages = []

    try:
        file_extension = Path(uploaded_file.name).suffix.lower()

        if file_extension == '.txt':
            passages = process_text_file(uploaded_file)
        elif file_extension == '.json':
            passages = process_json_file(uploaded_file)
        elif file_extension == '.csv':
            passages = process_csv_file(uploaded_file)
        elif file_extension == '.pdf':
            # Validate PDF first
            if not validate_pdf_file(uploaded_file):
                st.warning("PDF file may be corrupted or invalid. Attempting to process anyway.")
            passages = process_pdf_file(uploaded_file)
        else:
            raise ValueError(f"Unsupported file format: {file_extension}")

    except Exception as e:
        logger.error(f"Error processing file {uploaded_file.name}: {str(e)}")
        raise e

    return passages

def validate_pdf_file(uploaded_file) -> bool:
    """Validate PDF file before processing"""
    try:
        # Save current position
        current_pos = uploaded_file.tell()

        # Check if file is a PDF by magic number
        if uploaded_file.read(4) != b'%PDF':
            uploaded_file.seek(current_pos)
            return False
        uploaded_file.seek(current_pos)

        # Try to read the PDF
        import PyPDF2
        pdf_reader = PyPDF2.PdfReader(uploaded_file)
        if len(pdf_reader.pages) == 0:
            uploaded_file.seek(current_pos)
            return False

        uploaded_file.seek(current_pos)
        return True
    except Exception:
        return False

def process_text_file(uploaded_file) -> List[Dict[str, Any]]:
    """Process plain text files and split into passages"""
    content = uploaded_file.read().decode('utf-8')

    # Split text into paragraphs or sentences
    paragraphs = [p.strip() for p in content.split('\n\n') if p.strip()]

    passages = []
    for i, paragraph in enumerate(paragraphs):
        if len(paragraph) > 50:  # Filter out very short paragraphs
            passages.append({
                "id": f"{uploaded_file.name}_{i}",
                "text": paragraph,
                "source": uploaded_file.name,
                "metadata": {
                    "type": "text_file",
                    "paragraph_index": i,
                    "length": len(paragraph)
                }
            })

    return passages

def process_json_file(uploaded_file) -> List[Dict[str, Any]]:
    """Process JSON files containing medical data"""
    data = json.load(uploaded_file)
    passages = []

    if isinstance(data, list):
        # List of documents/passages
        for i, item in enumerate(data):
            passage = extract_passage_from_dict(item, f"{uploaded_file.name}_{i}", uploaded_file.name)
            if passage:
                passages.append(passage)
    elif isinstance(data, dict):
        # Single document or structured data
        if "passages" in data:
            # Structured format with passages array
            for i, passage_data in enumerate(data["passages"]):
                passage = extract_passage_from_dict(passage_data, f"{uploaded_file.name}_{i}", uploaded_file.name)
                if passage:
                    passages.append(passage)
        else:
            # Single document
            passage = extract_passage_from_dict(data, uploaded_file.name, uploaded_file.name)
            if passage:
                passages.append(passage)

    return passages

def process_csv_file(uploaded_file) -> List[Dict[str, Any]]:
    """Process CSV files containing medical data"""
    df = pd.read_csv(uploaded_file)
    passages = []

    # Try to identify text columns
    text_columns = []
    for col in df.columns:
        if any(keyword in col.lower() for keyword in ['text', 'content', 'passage', 'document', 'description', 'answer', 'response']):
            text_columns.append(col)

    if not text_columns:
        # If no obvious text columns, use all string columns
        text_columns = [col for col in df.columns if df[col].dtype == 'object']

    for idx, row in df.iterrows():
        text_parts = []
        metadata = {}

        for col in df.columns:
            if col in text_columns and pd.notna(row[col]):
                text_parts.append(str(row[col]))
            else:
                metadata[col] = row[col] if pd.notna(row[col]) else None

        if text_parts:
            combined_text = " ".join(text_parts)
            if len(combined_text.strip()) > 50:
                passages.append({
                    "id": f"{uploaded_file.name}_{idx}",
                    "text": combined_text.strip(),
                    "source": uploaded_file.name,
                    "metadata": {
                        "type": "csv_file",
                        "row_index": idx,
                        "columns_used": text_columns,
                        **metadata
                    }
                })

    return passages

def process_pdf_file(uploaded_file) -> List[Dict[str, Any]]:
    """Process PDF files with robust error handling and fallback methods"""
    passages = []

    try:
        # Method 1: Try PyPDF2 first
        try:
            import PyPDF2
            uploaded_file.seek(0)  # Reset file pointer
            pdf_reader = PyPDF2.PdfReader(uploaded_file)

            for page_num, page in enumerate(pdf_reader.pages):
                try:
                    text = page.extract_text()
                    if text and text.strip():
                        page_passages = extract_passages_from_text(
                            text, uploaded_file.name, page_num, "pdf_file"
                        )
                        passages.extend(page_passages)
                except Exception as page_error:
                    logger.warning(f"Error extracting text from page {page_num}: {str(page_error)}")
                    continue

        except Exception as pdf_error:
            logger.warning(f"PyPDF2 failed: {str(pdf_error)}")
            # Reset file pointer for next attempt
            uploaded_file.seek(0)

            # Method 2: Try pdfplumber if available
            try:
                import pdfplumber
                with pdfplumber.open(uploaded_file) as pdf:
                    for page_num, page in enumerate(pdf.pages):
                        try:
                            text = page.extract_text()
                            if text and text.strip():
                                page_passages = extract_passages_from_text(
                                    text, uploaded_file.name, page_num, "pdf_file"
                                )
                                passages.extend(page_passages)
                        except Exception as page_error:
                            logger.warning(f"pdfplumber error on page {page_num}: {str(page_error)}")
                            continue
            except ImportError:
                logger.warning("pdfplumber not available")
            except Exception as plumber_error:
                logger.error(f"pdfplumber also failed: {str(plumber_error)}")

        # If both methods failed, try a fallback approach
        if not passages:
            logger.info("Trying OCR fallback for PDF")
            passages = try_ocr_fallback(uploaded_file)

    except Exception as e:
        logger.error(f"Complete PDF processing failure: {str(e)}")
        return []

    return passages

def extract_passages_from_text(text: str, filename: str, page_num: int, file_type: str) -> List[Dict[str, Any]]:
    """Extract passages from text with proper cleaning"""
    passages = []

    # Clean text first to prevent regex issues
    text = clean_text(text)

    # Split text into paragraphs
    paragraphs = [p.strip() for p in re.split(r'\n\s*\n', text) if p.strip()]

    for para_idx, paragraph in enumerate(paragraphs):
        if len(paragraph) > 100:  # Filter out headers/footers/short fragments
            passages.append({
                "id": f"{filename}_page{page_num}_{para_idx}",
                "text": paragraph,
                "source": filename,
                "metadata": {
                    "type": file_type,
                    "page_number": page_num + 1,
                    "paragraph_index": para_idx,
                    "length": len(paragraph)
                }
            })

    return passages

def try_ocr_fallback(uploaded_file):
    """Fallback method using OCR for image-based PDFs"""
    passages = []
    try:
        # Check if OCR libraries are available
        try:
            import pytesseract
            from PIL import Image
            import io
        except ImportError:
            logger.error("OCR libraries not installed")
